query:
fragment AppErrorFragment on AppError {
  field
  message
  code
  permissions
  __typename
}

mutation AppTokenCreate($input: AppTokenInput!) {
  appTokenCreate(input: $input) {
    appToken {
      name
      authToken
      id
      __typename
    }
    authToken
    errors: appErrors {
      ...AppErrorFragment
      __typename
    }
    __typename
  }
}
