query:
fragment ProductErrorFragment on ProductError {
  code
  field
  __typename
}

mutation ProductImageReorder($productId: ID!, $imagesIds: [ID]!) {
  productImageReorder(productId: $productId, imagesIds: $imagesIds) {
    errors: productErrors {
      ...ProductErrorFragment
      __typename
    }
    product {
      id
      images {
        id
        alt
        sortOrder
        url
        __typename
      }
      __typename
    }
    __typename
  }
}
