query SearchCatalog($first: Int!, $query: String!) {
  categories(first: $first, filter: {search: $query}) {
    edges {
      node {
        id
        name
        __typename
      }
      __typename
    }
    __typename
  }
  collections(first: $first, filter: {search: $query}) {
    edges {
      node {
        id
        name
        isPublished
        publicationDate
        __typename
      }
      __typename
    }
    __typename
  }
  products(first: $first, filter: {search: $query}) {
    edges {
      node {
        id
        category {
          id
          name
          __typename
        }
        name
        __typename
      }
      __typename
    }
    __typename
  }
}
