query:
fragment InvoiceErrorFragment on InvoiceError {
  code
  field
  __typename
}

fragment InvoiceFragment on Invoice {
  id
  number
  createdAt
  url
  status
  __typename
}

mutation InvoiceEmailSend($id: ID!) {
  invoiceSendEmail(id: $id) {
    errors: invoiceErrors {
      ...InvoiceErrorFragment
      __typename
    }
    invoice {
      ...InvoiceFragment
      __typename
    }
    __typename
  }
}
