query:
fragment PageInfoFragment on PageInfo {
  endCursor
  hasNextPage
  hasPreviousPage
  startCursor
  __typename
}

fragment PermissionGroupFragment on Group {
  id
  name
  userCanManage
  users {
    id
    firstName
    lastName
    __typename
  }
  __typename
}

query PermissionGroupList($after: String, $before: String, $first: Int, $last: Int, $filter: PermissionGroupFilterInput, $sort: PermissionGroupSortingInput) {
  permissionGroups(after: $after, before: $before, first: $first, last: $last, filter: $filter, sortBy: $sort) {
    edges {
      node {
        ...PermissionGroupFragment
        __typename
      }
      __typename
    }
    pageInfo {
      ...PageInfoFragment
      __typename
    }
    __typename
  }
}
