query:
fragment PageInfoFragment on PageInfo {
  endCursor
  hasNextPage
  hasPreviousPage
  startCursor
  __typename
}

fragment SaleFragment on Sale {
  id
  name
  type
  startDate
  endDate
  value
  __typename
}

query SaleList($after: String, $before: String, $first: Int, $last: Int, $filter: SaleFilterInput, $sort: SaleSortingInput) {
  sales(after: $after, before: $before, first: $first, last: $last, filter: $filter, sortBy: $sort) {
    edges {
      node {
        ...SaleFragment
        __typename
      }
      __typename
    }
    pageInfo {
      ...PageInfoFragment
      __typename
    }
    __typename
  }
}
