query:
fragment PluginFragment on Plugin {
  id
  name
  description
  active
  __typename
}

fragment PluginsDetailsFragment on Plugin {
  ...PluginFragment
  configuration {
    name
    type
    value
    helpText
    label
    __typename
  }
  __typename
}

query Plugin($id: ID!) {
  plugin(id: $id) {
    ...PluginsDetailsFragment
    __typename
  }
}
