query Category($id: ID!) {
  category(id: $id) {
    seoDescription
    seoTitle
    id
    name
    backgroundImage {
      url
      __typename
    }
    ancestors(last: 5) {
      edges {
        node {
          id
          name
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
  attributes(filter: {inCategory: $id, filterableInStorefront: true}, first: 100) {
    edges {
      node {
        id
        name
        slug
        values {
          id
          name
          slug
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
