query:
fragment StaffErrorFragment on StaffError {
  code
  field
  __typename
}

fragment StaffMemberFragment on User {
  id
  email
  firstName
  isActive
  lastName
  __typename
}

fragment StaffMemberDetailsFragment on User {
  ...StaffMemberFragment
  permissionGroups {
    id
    name
    userCanManage
    __typename
  }
  userPermissions {
    code
    name
    __typename
  }
  avatar(size: 120) {
    url
    __typename
  }
  __typename
}

mutation StaffMemberUpdate($id: ID!, $input: StaffUpdateInput!) {
  staffUpdate(id: $id, input: $input) {
    errors: staffErrors {
      ...StaffErrorFragment
      __typename
    }
    user {
      ...StaffMemberDetailsFragment
      __typename
    }
    __typename
  }
}
