query AppsList($before: String, $after: String, $first: Int, $last: Int, $sort: AppSortingInput, $filter: AppFilterInput) {
  apps(before: $before, after: $after, first: $first, last: $last, sortBy: $sort, filter: $filter) {
    pageInfo {
      hasNextPage
      hasPreviousPage
      startCursor
      endCursor
      __typename
    }
    totalCount
    edges {
      node {
        id
        name
        isActive
        type
        __typename
      }
      __typename
    }
    __typename
  }
}
