query:
fragment AttributeFragment on Attribute {
  id
  name
  slug
  visibleInStorefront
  filterableInDashboard
  filterableInStorefront
  __typename
}

fragment PageInfoFragment on PageInfo {
  endCursor
  hasNextPage
  hasPreviousPage
  startCursor
  __typename
}

query AttributeList($filter: AttributeFilterInput, $before: String, $after: String, $first: Int, $last: Int, $sort: AttributeSortingInput) {
  attributes(filter: $filter, before: $before, after: $after, first: $first, last: $last, sortBy: $sort) {
    edges {
      node {
        ...AttributeFragment
        values {
          id
          name
          slug
          __typename
        }
        __typename
      }
      __typename
    }
    pageInfo {
      ...PageInfoFragment
      __typename
    }
    __typename
  }
}
