query:
fragment AccountError on AccountError {
  code
  field
  message
  __typename
}

mutation VerifyToken($token: String!) {
  tokenVerify(token: $token) {
    isValid
    payload
    user {
      id
      __typename
    }
    errors: accountErrors {
      ...AccountError
      __typename
    }
    __typename
  }
}
