query:
fragment SaleTranslationFragment on Sale {
  id
  name
  translation(languageCode: $language) {
    id
    language {
      code
      language
      __typename
    }
    name
    __typename
  }
  __typename
}

query SaleTranslationDetails($id: ID!, $language: LanguageCodeEnum!) {
  sale(id: $id) {
    ...SaleTranslationFragment
    __typename
  }
}
