query:
fragment PageInfoFragment on PageInfo {
  endCursor
  hasNextPage
  hasPreviousPage
  startCursor
  __typename
}

fragment PageTranslationFragment on Page {
  id
  contentJson
  seoDescription
  seoTitle
  title
  translation(languageCode: $language) {
    id
    contentJson
    seoDescription
    seoTitle
    title
    language {
      code
      language
      __typename
    }
    __typename
  }
  __typename
}

query PageTranslations($language: LanguageCodeEnum!, $first: Int, $after: String, $last: Int, $before: String, $filter: PageFilterInput) {
  pages(before: $before, after: $after, first: $first, last: $last, filter: $filter) {
    edges {
      node {
        ...PageTranslationFragment
        __typename
      }
      __typename
    }
    pageInfo {
      ...PageInfoFragment
      __typename
    }
    __typename
  }
}
