query:
fragment ShopErrorFragment on ShopError {
  code
  field
  __typename
}

fragment AddressFragment on Address {
  city
  cityArea
  companyName
  country {
    __typename
    code
    country
  }
  countryArea
  firstName
  id
  lastName
  phone
  postalCode
  streetAddress1
  streetAddress2
  __typename
}

fragment ShopFragment on Shop {
  authorizationKeys {
    key
    name
    __typename
  }
  companyAddress {
    ...AddressFragment
    __typename
  }
  countries {
    code
    country
    __typename
  }
  customerSetPasswordUrl
  defaultMailSenderAddress
  defaultMailSenderName
  description
  domain {
    host
    __typename
  }
  name
  __typename
}

mutation ShopSettingsUpdate($shopDomainInput: SiteDomainInput!, $shopSettingsInput: ShopSettingsInput!, $addressInput: AddressInput) {
  shopSettingsUpdate(input: $shopSettingsInput) {
    errors: shopErrors {
      ...ShopErrorFragment
      __typename
    }
    shop {
      ...ShopFragment
      __typename
    }
    __typename
  }
  shopDomainUpdate(input: $shopDomainInput) {
    errors: shopErrors {
      ...ShopErrorFragment
      __typename
    }
    shop {
      domain {
        host
        url
        __typename
      }
      __typename
    }
    __typename
  }
  shopAddressUpdate(input: $addressInput) {
    errors: shopErrors {
      ...ShopErrorFragment
      __typename
    }
    shop {
      companyAddress {
        ...AddressFragment
        __typename
      }
      __typename
    }
    __typename
  }
}
