query:
fragment PageTranslationFragment on Page {
  id
  contentJson
  seoDescription
  seoTitle
  title
  translation(languageCode: $language) {
    id
    contentJson
    seoDescription
    seoTitle
    title
    language {
      code
      language
      __typename
    }
    __typename
  }
  __typename
}

query PageTranslationDetails($id: ID!, $language: LanguageCodeEnum!) {
  page(id: $id) {
    ...PageTranslationFragment
    __typename
  }
}
