query:
fragment MenuErrorFragment on MenuError {
  code
  field
  __typename
}

fragment MenuItemFragment on MenuItem {
  category {
    id
    name
    __typename
  }
  collection {
    id
    name
    __typename
  }
  id
  level
  name
  page {
    id
    title
    __typename
  }
  url
  __typename
}

mutation MenuItemUpdate($id: ID!, $input: MenuItemInput!) {
  menuItemUpdate(id: $id, input: $input) {
    errors: menuErrors {
      ...MenuErrorFragment
      __typename
    }
    menuItem {
      ...MenuItemFragment
      __typename
    }
    __typename
  }
}
