query:
fragment CountryFragment on CountryDisplay {
  country
  code
  __typename
}

fragment CountryWithTaxesFragment on CountryDisplay {
  ...CountryFragment
  vat {
    standardRate
    reducedRates {
      rateType
      rate
      __typename
    }
    __typename
  }
  __typename
}

fragment ShopTaxesFragment on Shop {
  chargeTaxesOnShipping
  includeTaxesInPrices
  displayGrossPrices
  __typename
}

query CountryList {
  shop {
    ...ShopTaxesFragment
    countries {
      ...CountryWithTaxesFragment
      __typename
    }
    __typename
  }
}
