query:
fragment OrderErrorFragment on OrderError {
  code
  field
  __typename
}

mutation OrderShippingMethodUpdate($id: ID!, $input: OrderUpdateShippingInput!) {
  orderUpdateShipping(order: $id, input: $input) {
    errors: orderErrors {
      ...OrderErrorFragment
      __typename
    }
    order {
      availableShippingMethods {
        id
        name
        __typename
      }
      id
      shippingMethod {
        id
        name
        price {
          amount
          currency
          __typename
        }
        __typename
      }
      shippingMethodName
      shippingPrice {
        gross {
          amount
          currency
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
