query:
fragment DiscountErrorFragment on DiscountError {
  code
  field
  __typename
}

fragment SaleFragment on Sale {
  id
  name
  type
  startDate
  endDate
  value
  __typename
}

mutation SaleUpdate($input: SaleInput!, $id: ID!) {
  saleUpdate(id: $id, input: $input) {
    errors: discountErrors {
      ...DiscountErrorFragment
      __typename
    }
    sale {
      ...SaleFragment
      __typename
    }
    __typename
  }
}
