query:
fragment ShopTaxesFragment on Shop {
  chargeTaxesOnShipping
  includeTaxesInPrices
  displayGrossPrices
  __typename
}

mutation UpdateTaxSettings($input: ShopSettingsInput!) {
  shopSettingsUpdate(input: $input) {
    errors {
      field
      message
      __typename
    }
    shop {
      ...ShopTaxesFragment
      __typename
    }
    __typename
  }
}
