mutation UpdateAttributeValueTranslations($id: ID!, $input: NameTranslationInput!, $language: LanguageCodeEnum!) {
  attributeValueTranslate(id: $id, input: $input, languageCode: $language) {
    errors {
      field
      message
      __typename
    }
    attributeValue {
      id
      name
      translation(languageCode: $language) {
        id
        name
        __typename
      }
      __typename
    }
    __typename
  }
}
