query:
fragment PluginFragment on Plugin {
  id
  name
  description
  active
  __typename
}

fragment PluginsDetailsFragment on Plugin {
  ...PluginFragment
  configuration {
    name
    type
    value
    helpText
    label
    __typename
  }
  __typename
}

fragment PluginErrorFragment on PluginError {
  code
  field
  __typename
}

mutation PluginUpdate($id: ID!, $input: PluginUpdateInput!) {
  pluginUpdate(id: $id, input: $input) {
    errors: pluginsErrors {
      ...PluginErrorFragment
      __typename
    }
    plugin {
      ...PluginsDetailsFragment
      __typename
    }
    __typename
  }
}
