query:
fragment Address on Address {
  id
  firstName
  lastName
  companyName
  streetAddress1
  streetAddress2
  city
  postalCode
  country {
    code
    country
    __typename
  }
  countryArea
  phone
  isDefaultBillingAddress
  isDefaultShippingAddress
  __typename
}

fragment User on User {
  id
  email
  firstName
  lastName
  isStaff
  defaultShippingAddress {
    ...Address
    __typename
  }
  defaultBillingAddress {
    ...Address
    __typename
  }
  addresses {
    ...Address
    __typename
  }
  __typename
}

fragment AccountError on AccountError {
  code
  field
  message
  __typename
}

mutation CreateUserAddress($input: AddressInput!) {
  accountAddressCreate(input: $input) {
    errors: accountErrors {
      ...AccountError
      __typename
    }
    user {
      ...User
      __typename
    }
    __typename
  }
}
