query:
fragment AttributeFragment on Attribute {
  id
  name
  slug
  visibleInStorefront
  filterableInDashboard
  filterableInStorefront
  __typename
}

fragment MetadataItem on MetadataItem {
  key
  value
  __typename
}

fragment MetadataFragment on ObjectWithMetadata {
  metadata {
    ...MetadataItem
    __typename
  }
  privateMetadata {
    ...MetadataItem
    __typename
  }
  __typename
}

fragment AttributeDetailsFragment on Attribute {
  ...AttributeFragment
  ...MetadataFragment
  availableInGrid
  inputType
  storefrontSearchPosition
  valueRequired
  values {
    id
    name
    slug
    type
    __typename
  }
  __typename
}

fragment ProductErrorFragment on ProductError {
  code
  field
  __typename
}

mutation AttributeValueCreate($id: ID!, $input: AttributeValueCreateInput!) {
  attributeValueCreate(attribute: $id, input: $input) {
    attribute {
      ...AttributeDetailsFragment
      __typename
    }
    errors: productErrors {
      ...ProductErrorFragment
      __typename
    }
    __typename
  }
}
