query:
fragment PageFragment on Page {
  id
  title
  slug
  isPublished
  __typename
}

fragment MetadataItem on MetadataItem {
  key
  value
  __typename
}

fragment MetadataFragment on ObjectWithMetadata {
  metadata {
    ...MetadataItem
    __typename
  }
  privateMetadata {
    ...MetadataItem
    __typename
  }
  __typename
}

fragment PageDetailsFragment on Page {
  ...PageFragment
  ...MetadataFragment
  contentJson
  seoTitle
  seoDescription
  publicationDate
  __typename
}

query PageDetails($id: ID!) {
  page(id: $id) {
    ...PageDetailsFragment
    __typename
  }
}
