query:
fragment ExportFileFragment on ExportFile {
  id
  status
  url
  __typename
}

fragment ExportErrorFragment on ExportError {
  code
  field
  __typename
}

mutation ProductExport($input: ExportProductsInput!) {
  exportProducts(input: $input) {
    exportFile {
      ...ExportFileFragment
      __typename
    }
    errors: exportErrors {
      ...ExportErrorFragment
      __typename
    }
    __typename
  }
}
