query:
fragment PageTranslatableFragment on PageTranslatableContent {
  id
  contentJson
  seoDescription
  seoTitle
  title
  translation(languageCode: $language) {
    id
    contentJson
    seoDescription
    seoTitle
    title
    language {
      code
      language
      __typename
    }
    __typename
  }
  __typename
}

mutation UpdatePageTranslations($id: ID!, $input: PageTranslationInput!, $language: LanguageCodeEnum!) {
  pageTranslate(id: $id, input: $input, languageCode: $language) {
    errors {
      field
      message
      __typename
    }
    page {
      ...PageTranslatableFragment
      __typename
    }
    __typename
  }
}
