query:
fragment PageInfoFragment on PageInfo {
  endCursor
  hasNextPage
  hasPreviousPage
  startCursor
  __typename
}

fragment ProductTypeFragment on ProductType {
  id
  name
  hasVariants
  isShippingRequired
  taxType {
    description
    taxCode
    __typename
  }
  __typename
}

query ProductTypeList($after: String, $before: String, $first: Int, $last: Int, $filter: ProductTypeFilterInput, $sort: ProductTypeSortingInput) {
  productTypes(after: $after, before: $before, first: $first, last: $last, filter: $filter, sortBy: $sort) {
    edges {
      node {
        ...ProductTypeFragment
        __typename
      }
      __typename
    }
    pageInfo {
      ...PageInfoFragment
      __typename
    }
    __typename
  }
}
