query:
fragment PageInfoFragment on PageInfo {
  endCursor
  hasNextPage
  hasPreviousPage
  startCursor
  __typename
}

fragment SaleTranslationFragment on Sale {
  id
  name
  translation(languageCode: $language) {
    id
    language {
      code
      language
      __typename
    }
    name
    __typename
  }
  __typename
}

query SaleTranslations($language: LanguageCodeEnum!, $first: Int, $after: String, $last: Int, $before: String, $filter: SaleFilterInput) {
  sales(before: $before, after: $after, first: $first, last: $last, filter: $filter) {
    edges {
      node {
        ...SaleTranslationFragment
        __typename
      }
      __typename
    }
    pageInfo {
      ...PageInfoFragment
      __typename
    }
    __typename
  }
}
