query:
fragment CollectionTranslationFragment on Collection {
  id
  name
  descriptionJson
  seoDescription
  seoTitle
  translation(languageCode: $language) {
    id
    descriptionJson
    language {
      language
      __typename
    }
    name
    seoDescription
    seoTitle
    __typename
  }
  __typename
}

mutation UpdateCollectionTranslations($id: ID!, $input: TranslationInput!, $language: LanguageCodeEnum!) {
  collectionTranslate(id: $id, input: $input, languageCode: $language) {
    errors {
      field
      message
      __typename
    }
    collection {
      ...CollectionTranslationFragment
      __typename
    }
    __typename
  }
}
