fragment AddressFragment on Address {
  city
  cityArea
  companyName
  country {
    __typename
    code
    country
  }
  countryArea
  firstName
  id
  lastName
  phone
  postalCode
  streetAddress1
  streetAddress2
  __typename
}

fragment WarehouseFragment on Warehouse {
  id
  name
  __typename
}

fragment WarehouseWithShippingFragment on Warehouse {
  ...WarehouseFragment
  shippingZones(first: 100) {
    edges {
      node {
        id
        name
        __typename
      }
      __typename
    }
    __typename
  }
  __typename
}

fragment WarehouseDetailsFragment on Warehouse {
  ...WarehouseWithShippingFragment
  address {
    ...AddressFragment
    __typename
  }
  __typename
}

fragment WarehouseErrorFragment on WarehouseError {
  code
  field
  __typename
}

mutation WarehouseUpdate($id: ID!, $input: WarehouseUpdateInput!) {
  updateWarehouse(id: $id, input: $input) {
    errors: warehouseErrors {
      ...WarehouseErrorFragment
      __typename
    }
    warehouse {
      ...WarehouseDetailsFragment
      __typename
    }
    __typename
  }
}
