query:
fragment CollectionTranslationFragment on Collection {
  id
  name
  descriptionJson
  seoDescription
  seoTitle
  translation(languageCode: $language) {
    id
    descriptionJson
    language {
      language
      __typename
    }
    name
    seoDescription
    seoTitle
    __typename
  }
  __typename
}

query CollectionTranslationDetails($id: ID!, $language: LanguageCodeEnum!) {
  collection(id: $id) {
    ...CollectionTranslationFragment
    __typename
  }
}
