query:
fragment AppErrorFragment on AppError {
  field
  message
  code
  permissions
  __typename
}

fragment AppFragment on App {
  id
  name
  created
  isActive
  type
  homepageUrl
  appUrl
  configurationUrl
  supportUrl
  version
  accessToken
  privateMetadata {
    key
    value
    __typename
  }
  metadata {
    key
    value
    __typename
  }
  tokens {
    authToken
    id
    name
    __typename
  }
  webhooks {
    ...WebhookFragment
    __typename
  }
  __typename
}

fragment WebhookFragment on Webhook {
  id
  name
  isActive
  app {
    id
    name
    __typename
  }
  __typename
}

mutation AppUpdate($id: ID!, $input: AppInput!) {
  appUpdate(id: $id, input: $input) {
    app {
      ...AppFragment
      permissions {
        code
        name
        __typename
      }
      __typename
    }
    errors: appErrors {
      ...AppErrorFragment
      message
      permissions
      __typename
    }
    __typename
  }
}
