query:
fragment MainMenuSubItem on MenuItem {
  id
  name
  category {
    id
    name
    __typename
  }
  url
  collection {
    id
    name
    __typename
  }
  page {
    slug
    __typename
  }
  parent {
    id
    __typename
  }
  __typename
}

query MainMenu {
  shop {
    navigation {
      main {
        id
        items {
          ...MainMenuSubItem
          children {
            ...MainMenuSubItem
            children {
              ...MainMenuSubItem
              __typename
            }
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
