query:
fragment ProductTranslationFragment on Product {
  id
  name
  descriptionJson
  seoDescription
  seoTitle
  translation(languageCode: $language) {
    id
    descriptionJson
    language {
      code
      language
      __typename
    }
    name
    seoDescription
    seoTitle
    __typename
  }
  __typename
}

query ProductTranslationDetails($id: ID!, $language: LanguageCodeEnum!) {
  product(id: $id) {
    ...ProductTranslationFragment
    __typename
  }
}
