query:
fragment AttributeFragment on Attribute {
  id
  name
  slug
  visibleInStorefront
  filterableInDashboard
  filterableInStorefront
  __typename
}

fragment MetadataItem on MetadataItem {
  key
  value
  __typename
}

fragment MetadataFragment on ObjectWithMetadata {
  metadata {
    ...MetadataItem
    __typename
  }
  privateMetadata {
    ...MetadataItem
    __typename
  }
  __typename
}

fragment AttributeDetailsFragment on Attribute {
  ...AttributeFragment
  ...MetadataFragment
  availableInGrid
  inputType
  storefrontSearchPosition
  valueRequired
  values {
    id
    name
    slug
    type
    __typename
  }
  __typename
}

query AttributeDetails($id: ID!) {
  attribute(id: $id) {
    ...AttributeDetailsFragment
    __typename
  }
}
